mapper.select:

  		SELECT * FROM (
		    SELECT Tb.*, rownum rNum FROM (
		        SELECT * FROM reviewboard ORDER BY rv_idx DESC
		    ) Tb
		)
		WHERE rNum BETWEEN #{param1} AND #{param2}

SELECT * FROM (
SELECT Tb.*, rownum rNum FROM (
SELECT * FROM reviewboard 

		WHERE ${searchField} like '%'||#{searchTxt}||'%'


		ORDER BY rv_idx DESC
	) Tb
)
WHERE rNum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE reviewboard SET title =#{title}, contents=#{contents}, 
rv_sfile1=#{rv_sfile1}, rv_ofile1=#{rv_ofile1},
rv_sfile2=#{rv_sfile2}, rv_ofile2=#{rv_ofile2},
rv_sfile3=#{rv_sfile3}, rv_ofile3=#{rv_ofile3},
writer=#{writer}, or_idx=#{or_idx}
WHERE rv_idx=#{rv_idx}
